import React, { Component } from 'react'
import ListItemCard from './ListItemCard'

var addCard = require('../../images/Add.png');

export class ListItemsTable extends Component {
    moveUp = (itemName) =>
    {
        window.alert("We are in Move Up: " + itemName);
    }

    moveDown = (itemName) =>
    {
        window.alert("We are in Move Down: " + itemName);
    }

    deleteItem = (itemName) =>
    {
        window.alert("We are in Delete Item: " + itemName);
    }


    render() {
        return (
            <div id="list_items_container">
                <div className="list_item_header_card">
                    <div className="list_item_task_header">Task</div>
                    <div className="list_item_due_date_header">Due Date</div>
                    <div className="list_item_status_header">Status</div>
                </div>

                {
                    this.props.todoList.items.map((todoItem) => (
                        <ListItemCard
                            key={todoItem.key}
                            listItem={todoItem}
                            listUp={this.moveUp}
                            listDown={this.moveDown}
                            deleteItem={this.deleteItem}/>
                    ))
                }
                <div className='list_item_add_card'>
                    <img src={addCard} className="list_item_add_card" />
                </div>
            </div>
        )
    }
}

export default ListItemsTable
